  @Input() size?: FabSize;
  @Input() type?: FabType;

  active: boolean = false;

  closeIcon = 'close';

  fabStyle = '';

  @HostListener('click') myClick() {
    if (this.type == 'normal') {
      this.active = !this.active;
    }
  }

  constructor() {}

  ngOnInit() {
    this.fabStyle = this.setStyles();
  }

  setStyles() {
    let style = 'bmb_fab';

    if (this.size == 'small') {
      style = `${style}-small`;
    } else {
      style = `${style}-large`;
    }

    if (this.type === 'extended') {
      style = `${style}-extended`;
    } else {
      style = `${style}-normal`;
    }
    return style;
  }
}

