import {
  Component,
  Input,
  ViewEncapsulation,
  ChangeDetectionStrategy,
  ElementRef,
  Renderer2,
  AfterViewInit,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { BmbIconComponent } from '../bmb-icon/bmb-icon.component';
@Component({
  selector: 'bmb-tag',
  standalone: true,
  imports: [CommonModule, BmbIconComponent],
  styleUrl: './bmb-tags.component.scss',
  templateUrl: './bmb-tags.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None,
})
export class BmbTagComponent implements AfterViewInit {
  @Input() appearance: string = '';
  @Input() text: string = '';
  @Input() grouped: boolean = false;
  @Input() dissmisable: boolean = false;

  constructor(
    private el: ElementRef,
    private renderer: Renderer2,
  ) {}

  ngAfterViewInit() {
    if (this.grouped) {
      const parentElement = this.el.nativeElement.parentElement;

      let wrapperDiv = parentElement.querySelector('.bmb_tag-grouped');
      if (!wrapperDiv) {
        wrapperDiv = this.renderer.createElement('div');
        this.renderer.addClass(wrapperDiv, 'bmb_tag-grouped');
        this.renderer.insertBefore(parentElement, wrapperDiv, null);
      }

      this.renderer.appendChild(wrapperDiv, this.el.nativeElement);
    }
  }

  getClasses(): string[] {
    const classes: string[] = ['bmb_tag'];

    if (this.appearance) {
      classes.push('bmb_tag-' + this.appearance);
    }

    return classes;
  }

  closeTag() {
    this.el.nativeElement.remove();
  }
}

import { Component, OnInit, inject, ViewEncapsulation } from '@angular/core';
import { ThemeService } from '../../services';
import { CommonModule } from '@angular/common';
import { BmbSwitchComponent } from '../bmb-switch/bmb-switch.component';

@Component({
  selector: 'bmb-theme',
  standalone: true,
  imports: [CommonModule, BmbSwitchComponent],
  providers: [ThemeService],
  templateUrl: './bmb-theme.component.html',
  encapsulation: ViewEncapsulation.None,
})
export class BmbThemeComponent implements OnInit {
  selectedTheme: string = 'light';

  private service = inject(ThemeService);

  ngOnInit(): void {
    const savedTheme = localStorage.getItem('theme') || 'light';
    this.selectedTheme = savedTheme;
    this.applyTheme(savedTheme);

    this.service.theme$.subscribe((theme: any) => {
      this.applyTheme(theme);
    });
  }

  applyTheme(theme: string): void {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('theme', theme);
  }

  onThemeChange(isChecked: boolean): void {
    this.selectedTheme = isChecked ? 'dark' : 'light';
    localStorage.setItem('theme', this.selectedTheme);
    this.service.setTheme(this.selectedTheme);
  }
}

import {
  Component,
  Input,
  ChangeDetectionStrategy,
  OnInit,
  ViewEncapsulation,
  OnDestroy,
} from '@angular/core';
import { ToastService } from '../../services';
import { Subscription } from 'rxjs';
import { CommonModule } from '@angular/common';
import { BmbIconComponent } from '../bmb-icon/bmb-icon.component';

@Component({
  standalone: true,
  selector: 'bmb-toast',
  styleUrl: './bmb-toast.component.scss',
  imports: [CommonModule, BmbIconComponent],
  templateUrl: './bmb-toast.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [ToastService],
  encapsulation: ViewEncapsulation.None,
})
export class BmbToastComponent implements OnInit, OnDestroy {
  @Input() appearance: string = '';
  @Input() message: string = '';
  @Input() duration?: number;
  @Input() position?: 'top' | 'bottom' | 'middle';

  isOpen$ = this.toastService.isOpen$;

  private isOpenSubscription: Subscription | undefined;

  constructor(private toastService: ToastService) {}

  ngOnInit(): void {
    this.isOpenSubscription = this.isOpen$.subscribe((isOpen) => {
      if (isOpen && this.duration) {
        this.setAutoClose();
      }
    });
  }

  ngOnDestroy(): void {
    if (this.isOpenSubscription) {
      this.isOpenSubscription.unsubscribe();
    }
  }

  getClasses(): string[] {
    const classes: string[] = ['bmb_toast'];

    if (this.appearance) {
      classes.push('bmb_toast-' + this.appearance);
    }

    if (this.position) {
      classes.push('bmb_toast-' + this.position);
    }

    return classes;
  }

  private setAutoClose(): void {
    setTimeout(() => {
      this.toastService.closeToast();
    }, this.duration);
  }

  public openToast() {
    this.toastService.openToast();
  }
}

