@Component({
  selector: 'bmb-tabs',
  templateUrl: './bmb-tabs.component.html',
  styleUrls: ['./bmb-tabs.component.scss'],
  standalone: true,
  imports: [CommonModule],
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None,
})
export class BmbTabsComponent implements OnInit, AfterViewInit {
  @Input() format: string = '';
  @Input() tabs: IBmbTab[] = [];
  @Output() selected = new EventEmitter<IBmbTab>();

  activeTabIndex: number = 0;
  @ViewChild('tabsItems') tabsItems!: ElementRef;

  ngOnInit(): void {
    const initialActiveTab = this.tabs.findIndex((tab) => tab.isActive);
    this.activeTabIndex = initialActiveTab !== -1 ? initialActiveTab : 0;
    this.tabs.forEach(
      (tab, index) => (tab.isActive = index === this.activeTabIndex),
    );
  }

  ngAfterViewInit(): void {
    setTimeout(() => this.showActiveTab(), 100);
  }

  selectTab(selectedId: number): void {
    const tabIndex = this.tabs.findIndex((tab) => tab.id === selectedId);
    if (tabIndex !== -1) {
      this.tabs.forEach((tab) => (tab.isActive = false));
      this.tabs[tabIndex].isActive = true;
      this.activeTabIndex = tabIndex;
      const activeTab = this.tabs[tabIndex];
      this.selected.emit(activeTab);
      setTimeout(() => this.showActiveTab(), 0);
    }
  }

  showActiveTab(): void {
    requestAnimationFrame(() => {
      const tabsElement = this.tabsItems.nativeElement;
      const activeTabElement = tabsElement.children[this.activeTabIndex];

      if (activeTabElement) {
        const containerWidth = tabsElement.offsetWidth;
        const activeTabWidth = activeTabElement.offsetWidth;
        const activeTabOffsetLeft = activeTabElement.offsetLeft;

        const scrollOffset =
          activeTabOffsetLeft - (containerWidth - activeTabWidth) / 2;

        tabsElement.scrollTo({
          left: scrollOffset,
          behavior: 'smooth',
        });
      }
    });
  }
}

interface IFixDecimals {
  value: number;
  dp?: number;
}

function fixDecimals({ value, dp = 2 }: IFixDecimals) {
  return parseFloat(value.toString()).toFixed(dp);
}

export const pixelToRem = (size: number) =>
  `${fixDecimals({ value: size / 16 })}rem`;

import { Directive, HostBinding, Input, OnInit } from '@angular/core';

export interface IMargin {
  sm: number;
  lg?: number;
}

@Directive({
  selector: '[bmbLayoutItem]',
  standalone: true,
})
export class BmbLayoutItemDirective implements OnInit {
  @Input() colSm: number = 0;
  @Input() colLg: number = 0;
  @Input() marginLeft?: IMargin;
  @Input() marginRight?: IMargin;
  @Input() colGrow: number = 0;
  @Input() isDinamycItem: boolean = false;

  @HostBinding('class') get elementClass(): string[] {
    const classes = [];

    if (this.colSm) classes.push(`bmb_col-sm-${this.colSm}`);
    if (this.colLg) classes.push(`bmb_col-lg-${this.colLg}`);
    if (this.marginLeft) {
      classes.push(`bmb_space-sm-left-${this.marginLeft?.sm}`);

      if (this.marginLeft.lg)
        classes.push(`bmb_space-lg-left-${this.marginLeft?.lg}`);
    }
    if (this.marginRight) {
      classes.push(`bmb_space-sm-right-${this.marginRight?.sm}`);

      if (this.marginRight.lg)
        classes.push(`bmb_space-lg-right-${this.marginRight?.lg}`);
    }
    return classes;
  }

  @HostBinding('style.flex') flex?: string;

  ngOnInit() {
    if (this.isDinamycItem) {
      this.flex = `${this.colGrow} 0 0%`;
    }
  }
}

