import { Directive, HostBinding, Input } from '@angular/core';
import { SizeNames } from '../../types';

export type IJustifyOptions =
  | 'center'
  | 'end'
  | 'start'
  | 'stretch'
  | 'spaceAround'
  | 'spaceBetween'
  | 'spaceEvenly';
export type IAlignItemsOptions = 'center' | 'end' | 'start' | 'stretch';

@Directive({
  selector: '[bmbLayout]',
  standalone: true,
})
export class BmbLayoutDirective {
  @Input() gapSise: SizeNames = 'm';
  @Input() margin: SizeNames = 'm';
  @Input() dinamycCols: boolean = false;
  @Input() justify: IJustifyOptions = 'start';
  @Input() alignItems: IAlignItemsOptions = 'start';

  constructor() {}

  @HostBinding('class') get elementClass(): string[] {
    const classes = [
      'bmb_layout',
      `bmb_gap-${this.gapSise}`,
      `bmb_margin-${this.margin}`,
      `bmb_justify-${this.justify}`,
      `bmb_alignItems-${this.alignItems}`,
    ];

    if (this.dinamycCols) classes.push('bmb_layout-smart');
    return classes;
  }
}

import { Directive, HostBinding, Input, OnInit } from '@angular/core';

export interface IMargin {
  sm: number;
  lg?: number;
}

@Directive({
  selector: '[bmbLayoutItem]',
  standalone: true,
})
export class BmbLayoutItemDirective implements OnInit {
  @Input() colSm: number = 0;
  @Input() colLg: number = 0;
  @Input() marginLeft?: IMargin;
  @Input() marginRight?: IMargin;
  @Input() colGrow: number = 0;
  @Input() isDinamycItem: boolean = false;

  @HostBinding('class') get elementClass(): string[] {
    const classes = [];

    if (this.colSm) classes.push(`bmb_col-sm-${this.colSm}`);
    if (this.colLg) classes.push(`bmb_col-lg-${this.colLg}`);
    if (this.marginLeft) {
      classes.push(`bmb_space-sm-left-${this.marginLeft?.sm}`);

      if (this.marginLeft.lg)
        classes.push(`bmb_space-lg-left-${this.marginLeft?.lg}`);
    }
    if (this.marginRight) {
      classes.push(`bmb_space-sm-right-${this.marginRight?.sm}`);

      if (this.marginRight.lg)
        classes.push(`bmb_space-lg-right-${this.marginRight?.lg}`);
    }
    return classes;
  }

  @HostBinding('style.flex') flex?: string;

  ngOnInit() {
    if (this.isDinamycItem) {
      this.flex = `${this.colGrow} 0 0%`;
    }
  }
}

import { DOCUMENT } from '@angular/common';
import {
  AfterViewInit,
  Directive,
  ElementRef,
  EventEmitter,
  Inject,
  OnDestroy,
  Output,
} from '@angular/core';
import { Subscription, filter, fromEvent } from 'rxjs';

@Directive({
  selector: '[clickOutside]',
  standalone: true,
})
export class ClickOutsideDirective implements AfterViewInit, OnDestroy {
  @Output() clickOutside: EventEmitter<void> = new EventEmitter<void>();

  documentClickSubscription: Subscription | undefined;

  constructor(
    private element: ElementRef,
    @Inject(DOCUMENT) private document: Document,
  ) {}

  ngAfterViewInit(): void {
    this.documentClickSubscription = fromEvent(this.document, 'click')
      .pipe(
        filter((event) => {
          return !this.isInside(event.target as HTMLElement);
        }),
      )
      .subscribe(() => {
        this.clickOutside.emit();
      });
  }

  isInside(elemeToCheck: HTMLElement): boolean {
    return (
      elemeToCheck === this.element.nativeElement ||
      this.element.nativeElement.contains(elemeToCheck) ||
      elemeToCheck?.classList?.contains('modal-persist')
    );
  }

  ngOnDestroy(): void {
    this.documentClickSubscription?.unsubscribe();
  }
}

