@use "../../../assets/styles/base/fonts" as fonts;
@use "../../../assets/styles/base/mixins" as mixins;
@use "../../../assets/styles/base/rem" as rem;

@mixin input($type: main) {
  @include mixins.resetElement();
  @include fonts.font(4, regular);
  appearance: none;
  height: rem.rem_calc(48);
  width: 100%;

  &:focus,
  &:focus:hover,
  &:disabled:focus {
    @include mixins.focusElement();
  }

  @if $type == simple {
    background-color: transparent;
    border: 0;
    border-bottom: rem.rem_calc(1) solid RGBA(var(--color-charade-950));
    color: RGBA(var(--color-charade-700));

    &:focus {
      border-bottom: rem.rem_calc(2) solid RGBA(var(--color-blue-primary));
      border-color: RGBA(var(--color-blue-primary));
      color: RGBA(var(--color-mariner-700));
      outline: none;
    }

    &::placeholder {
      color: RGBA(var(--color-charade-300));
    }

    &.bmb_field-input-error {
      border-bottom-color: RGBA(var(--color-red-primary));
    }
  } @else if $type == normal {
    background-color: RGBA(var(--color-charade-50));
    border: rem.rem_calc(1) solid RGBA(var(--color-charade-500));
    border-radius: rem.rem_calc(8);
    color: RGBA(var(--color-charade-950));
    padding: var(--bmb-spacing-s);

    &:focus {
      border: rem.rem_calc(2) solid RGBA(var(--color-mariner-700));
      outline: none;
    }

    &::placeholder {
      color: RGBA(var(--color-charade-300));
    }

    &.bmb_field-input-error {
      border-color: RGBA(var(--color-red-primary)) !important;
    }
  }
}

.bmb_field {
  &-label {
    color: RGBA(var(--color-charade-950));
    display: flex;
    margin: rem.rem_calc(2 2 4 8);
    @include fonts.font(3, regular);

    &-required {
      color: RGBA(var(--color-red-light));
    }
  }

  &-wrapper {
    align-items: center;
    display: flex;
    position: relative;
    width: 100%;
  }

  &-error {
    @include fonts.font(3, regular);
    color: RGBA(var(--color-red-light));
    margin: rem.rem_calc(8);
  }

  &-icon {
    color: RGBA(var(--color-charade-500));
    font-size: rem.rem_calc(24);
    left: rem.rem_calc(8);
    position: absolute;
    top: 50%;
    transform: translateY(-50%);

    ~ .bmb_field-input {
      padding-left: rem.rem_calc(35);
    }
  }

  &-helper {
    @include fonts.font(3, regular);
    color: RGBA(var(--color-charade-950));
    margin: rem.rem_calc(8);
  }

  &-input {
    margin: 0;

    &-normal {
      @include input(normal);
    }

    &-simple {
      @include input(simple);
    }

    &-main {
      @include input(main);
    }

    &.disabled {
      pointer-events: none;
      opacity: 0.8;
    }
  }
}

[data-theme="dark"],
.storybook-dark-theme {
  .bmb_field {
    &-label {
      color: RGBA(var(--color-charade-50));
    }

    &-input-normal {
      background-color: RGBA(var(--color-charade-950));
      color: RGBA(var(--color-charade-200));

      &:hover {
        border-color: RGBA(var(--color-charade-500));
      }

      &:focus {
        border-color: RGBA(var(--color-charade-50));
        color: RGBA(var(--color-charade-50));
      }
    }

    &-icon {
      color: RGBA(var(--color-charade-50));
    }

    &-helper {
      color: RGBA(var(--color-charade-300));
    }

    &-input-simple {
      border-bottom: rem.rem_calc(1) solid RGBA(var(--color-charade-700));
      color: RGBA(var(--color-charade-200));

      &:hover {
        border-color: RGBA(var(--color-charade-700));
      }

      &:focus {
        border-bottom: rem.rem_calc(2) solid RGBA(var(--color-charade-50));
        color: RGBA(var(--color-charade-50));
      }
    }
  }
}

