@use "../../../assets/styles/base/fonts" as fonts;
@use "../../../assets/styles/base/rem" as rem;
@use "../../../assets/styles/base/mixins" as mixins;

.bmb_table {
  background: RGBA(var(--color-charade-50));
  border-collapse: collapse;
  box-shadow: rem.rem_calc(8 8 8 0) RGBA(var(--color-charade-950), 0.5);
  display: flex;
  flex-direction: column;
  padding: rem.rem_calc(24 16);
  width: 100%;

  &-container {
    flex: 1 0 auto;
    max-width: 100%;
    overflow: auto;
    width: 100%;
  }

  &-content {
    width: 100%;
  }

  &-row {
    &.mat-mdc-row {
      height: rem.rem_calc(34);
    }

    > td,
    > th {
      height: auto;
      max-width: rem.rem_calc(100);
      overflow: hidden;
      padding: rem.rem_calc(0 8);
      text-overflow: ellipsis;
      white-space: nowrap;

      @include mixins.media-query(768, "min") {
        max-width: fit-content;
      }

      &.bmb_table-checkbox {
        min-width: rem.rem_calc(58);
        padding-right: 0;
        width: rem.rem_calc(58);

        + .bmb_table-sticky {
          left: rem.rem_calc(58) !important;
        }
      }

      &.bmb_table-expand {
        min-width: rem.rem_calc(40);
        width: rem.rem_calc(40);

        + .bmb_table-sticky {
          left: rem.rem_calc(98) !important;
        }
      }

      &.bmb_table-sticky {
        border-right: rem.rem_calc(1) solid RGBA(var(--color-charade-300));
        max-width: rem.rem_calc(144);

        @include mixins.media-query(1200, "min") {
          max-width: fit-content;
        }
      }

      &.bmb_table-actions {
        border-left: rem.rem_calc(1) solid RGBA(var(--color-charade-300));
        color: RGBA(var(--color-charade-900));

        button {
          color: RGBA(var(--color-charade-900));
        }
      }
    }

    > th {
      border-bottom: 0;
      color: RGBA(var(--color-charade-500));
      position: relative;
      text-transform: uppercase;
      @include fonts.font(4, medium);

      &.mat-mdc-header-cell {
        background-color: RGBA(var(--color-charade-50));
      }
    }

    > td {
      @include fonts.font(4, medium);
      border-bottom-color: RGBA(var(--color-charade-200));
      border-bottom-style: dashed;

      &.bmb_table-actions {
        .action-container {
          display: flex;

          bmb-icon {
            font-size: rem.rem_calc(18);
          }
        }
      }

      .mat-mdc-icon-button {
        align-items: center;
        display: flex;
        height: auto;
        padding: 0;
        width: auto;

        bmb-icon {
          font-size: rem.rem_calc(24);
        }
      }
    }
  }

  &-even {
    &.mat-mdc-row {
      background-color: RGBA(var(--color-charade-50));
      color: RGBA(var(--color-charade-950));

      &.bmb_table-expandible {
        background-color: RGBA(var(--color-mariner-100));
      }
    }
  }

  &-odd {
    &.mat-mdc-row {
      background-color: RGBA(var(--color-charade-100));
      color: RGBA(var(--color-charade-700));
    }

    &.bmb_table-expandible {
      background-color: RGBA(var(--color-mariner-100));
    }
  }

  &-details {
    &.mat-mdc-row {
      background-color: RGBA(var(--color-mariner-100));
      height: 0;

      > td {
        border: 0;
        padding: rem.rem_calc(0 16);
      }

      &.bmb_table-expanded {
        > td {
          border-bottom: rem.rem_calc(1) dashed RGBA(var(--color-charade-200));
          padding: rem.rem_calc(16);
        }
      }
    }
  }

  &-detail {
    color: RGBA(var(--color-charade-700));
    display: flex;
    overflow: hidden;
  }

  &-resize {
    border-right: rem.rem_calc(1) solid RGBA(var(--color-charade-200));
    cursor: col-resize;
    display: inline-block;
    height: 100%;
    opacity: 0;
    position: absolute;
    right: 0;
    top: 0;

    &:hover {
      opacity: 1;
      width: rem.rem_calc(20);
    }
  }

  .mat-mdc-paginator {
    display: none;
  }

  &-paginator {
    align-items: center;
    bottom: 0;
    display: flex;
    height: rem.rem_calc(48);
    justify-content: flex-end;
    margin: rem.rem_calc(8 0);
    position: sticky;
    right: 0;
    z-index: 10;

    &-text {
      color: RGBA(var(--color-charade-700));
      margin-right: rem.rem_calc(32);
