  circunference = 2 * Math.PI * this.radius;
  dashoffset = 0;
  symbol = '%';

  constructor() {
    this.progress(0);
  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes['percentValue']) {
      if (changes['percentValue'].currentValue > 100) {
        this.progress(100);
